sites.css:
/* Block those idiot chat popups */

body { overflow: scroll !important; }

.intercom-app,.intercom-lightweight-app,.intercom-frame {
	display:none;
}

.cc-window {
	display: none !important;
}

.chat-widget-container, #chat-widget-container {
	display: none !important;
}

.zopim { display: none !important; }

/* Block those stupid GDPR boxes that pop up every time you visit a new site. Terrible UX we have on the internet today. If I don't see them and don't hit consent then I never consented, ha! */

#consent_blackbar {
	display: none !important;
}

.gdpr, .gdpr-privacy-bar {
	display: none !important;
}

.onetrust-consent-sdk {
	display: none !important;
}

.ensNotifyBanner { display: none !important; }

#gdpr-cookie-message {display: none !important; }

#ccpa-banner {display:none !important; }
.ccpa-wrapper {display: none !important; }

#cookie-law-info-bar, #cookie-law-info-again {display: none !important; }

.cli-modal-backdrop {display: none !important; }

/* Banners */

#hello_banner { display:none !important; }
.grecaptcha-badge {display: none !important; }

/* Block any "modal" stuff. We stopped window popups a long time ago for a good reason and they interrupt my attention and people are unfortunately now using modals to circumvent that */

.ml-webforms-popup-overlay { display: none !important; }
.soundest-forms-container { display: none !important; }

.signup-modal {
	display: none !important;
}

.tp-modal, .tp-backdrop {
    display:none !important;
}

.tp-modal-open {
	overflow:scroll !important;
	overflow-x:scroll !important;
	overflow-y:scroll !important;
}

.colorBox, .cBoxOverlay {display: none !important;}

.modal-x, .js-modal {
	display: none !important;
}

.mc-modal, .mc-modal-big, .mc-modal-bg, .mc-banner {
	display: none !important;
}

/* .modal, .modal-backdrop {display:none !important;} */

.bx-base, .bx-slab, .bx-align {
	display: none !important;
}

.bx-client-overlay {
	overflow:scroll !important;
	overflow-x:scroll !important;
	overflow-y:scroll !important;
}

.pop-up-wrapper {
display: none !important;
}

.ncm-container { display:none !important; }

.CampaignType--popup { display: none !important; }

/* Block those ads that masquerade as link recommendations. */

.ad-container-wrapper, .ad-container {
	display: none !important;
}

.m-ad, .img_ad, .outbrain-container, .adunitContainer, .adBox, .vendor-taboola, .tbl-feed-container {
	display: none !important;
}

.ob-dynamic-rec-container, .OUTBRAIN {
	display: none !important;
}

.stackcommerce__container, .stackcommerce__container--article {
	display: none !important;
}

.adslot, .adslot-medrec, .SideBar__ad {
	display: none !important;
}

.ac-lre-player-ph {
	display: none !important;
}

.right-column-ads {
	display: none !important;
}

.ad, ad-wrapper {
	display: none !important;
}

.taboola, .taboola-container, .trc_rbox_container {
	display: none !important;
}

#adblock-detection, #adblock-detection-content {
    display:none !important;
}

sites.js:
setInterval(()=> {
	/* Remove modal-related classes from document.body to avoid scrolling getting disabled */
	document.body.classList.remove('tp-modal-open');
	document.body.classList.remove('bx-client-overlay');
	document.body.classList.remove('pop-up-open');
	document.body.classList.remove('modal-open');
	document.body.parentElement.classList.remove('om-position-popup');
}, 1000);

